mapper.update:
update recipeboard set title=#{title}, sub_title=#{subTitle}, recipe_name=#{recipeName}, time=#{time},
 cost=#{cost}, content=#{content}, servings=#{servings}, reg_date=sysdate
where no=#{no}

mapper.select:
select X.rnum, X.no, X.user_no as userNo, X.title, x.sub_title as subTitle, X.time, X.cost, X.content , X.user_nickname as userNickName, X.recipe_name as recipeName, to_char(X.reg_date, 'yyyy-MM-dd') as regDate , X.view_cnt as viewCnt, X.servings from ( select rownum as rnum, A.no, A.user_no, A.title, A.sub_title, A.time, A.cost, A.content , A.user_nickname, A.recipe_name, A.reg_date, A.view_cnt, A.servings from ( select b.no, b.user_no, b.title, b.sub_title, b.time, b.cost, b.content, b.user_nickname, b.recipe_name, b.reg_date, b.view_cnt, b.servings
from recipeboard b, recipe_tags r, tags t where b.no = r.r_no and t.no = r.t_no and t.name = #{TagName}
order by no desc) A where rownum <= #{startPage}+(#{numPerPage}-1)) X where X.rnum >= #{startPage}

mapper.update:
update recipeboard 
set point = (point+1) 
where no = #{receipeNo}

update ruser
set point = (point+1) 
where no = #{receipeUserNo}

update recipeboard 
set point = (point-1) 
where no = #{receipeNo}

update ruser
set point = (point-1) 
where no = #{receipeUserNo}

mapper.delete:
delete from rpoint
where user_no = #{userNo}
and receipe_user_no = #{receipeUserNo}
and receipe_no =  #{receipeNo}
and type = 'U'

delete from rpoint
where user_no = #{userNo}
and receipe_user_no = #{receipeUserNo}
and receipe_no =  #{receipeNo}
and type = 'D'

mapper.select:
select count(*) 
from(select * from rpoint
    where user_no = #{userNo}
    and receipe_no =  #{receipeNo}
    and type = 'U')

select count(*) 
from(select * from rpoint
    where user_no = #{userNo}
    and receipe_no =  #{receipeNo}
    and type = 'D')

mapper.update:
update ruser 
set follower_cnt = (follower_cnt+1) 
where no = #{receipeUserNo}

update ruser 
set follower_cnt = (follower_cnt-1) 
where no = #{receipeUserNo}

mapper.delete:
delete from rfollow
where follower = #{userNo}
and followee = #{receipeUserNo}
and type = 'F'

mapper.select:
select count(*) 
from(select * from rfollow
    where follower = #{userNo}
    and followee =  #{receipeUserNo}
    and type = 'F')
